package coadingpractice;

public class IfElseStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
		int marks=80;
		
		if(marks>70)
		{
		System.out.println(" Grade A+ ");
		}
		else if(marks>60)
		{
		System.out.println(" Grade A");
		}
		else 
		{
		System.out.println("Fail");
		}

		
	}
}
